CREATE TABLE `pap_berita_lain` (
  `id` int(5) NOT NULL,
  `kat` varchar(200) NOT NULL,
  `judul` text CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `slug` varchar(195) NOT NULL,
  `permalink` varchar(200) NOT NULL,
  `isi` longtext CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `file` text DEFAULT NULL,
  `hits` int(5) NOT NULL DEFAULT 0,
  `whoentri` varchar(25) NOT NULL DEFAULT '',
  `whereentri` text NOT NULL,
  `whenentri` datetime NOT NULL,
  `whopublish` varchar(25) DEFAULT NULL,
  `wherepublish` text DEFAULT NULL,
  `whenpublish` datetime DEFAULT NULL,
  `whoubah` varchar(25) DEFAULT NULL,
  `whereubah` text DEFAULT NULL,
  `whenubah` datetime DEFAULT NULL,
  `published` enum('y','t') NOT NULL DEFAULT 'y'
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

