(2, 2, 't', NULL, NULL, NULL, 'Visi<br/> Politeknik Akamigas Palembang', 'Terwujudnya Institusi Yang Unggul Secara Nasional di Bidang Energi<br/>dan Berdaya Saing Global Tahun 2025', 'slide21.jpg', 'video2.mp4', NULL, 0, 'root', '127.0.0.1', '2022-01-06 11:27:54', NULL, NULL, NULL, 'y'),
(3, 2, 't', NULL, NULL, NULL, 'Misi<br/> Politeknik Akamigas Palembang', 'Melaksanakan Tridharma Perguruan Tinggi yang kontributif dan produktif di tingkat nasional.<br/>\r\nMemenuhi kebutuhan SDM yang tersertifikasi dan berdaya saing tinggi di tingkat ASEAN.<br/>\r\nMewujudkan kehidupan civitas akademika yang sejahtera, optimal dan kondusif serta bersinergi dengan pihak terkait.', 'slide22.jpg', 'video3.mp4', NULL, 0, 'root', '127.0.0.1', '2022-01-06 11:27:54', NULL, NULL, NULL, 'y');

