INSERT INTO `pap_testimonial` (`id`, `nama`, `alumni`, `kerja`, `prodi`, `testimoni`, `urut`, `publish`) VALUES
(1, 'Erik Saputra', 2015, 'PT Bukit Asam, Tbk', 4, 'Salam Akamigas! saya alumni Politeknik Akamigas Palembang, lulus di tahun 2015 dan saat ini sudah bekerja di Badan Usaha Milik Negara (BUMN) di PT. Bukit Asam Persero, Tbk', 1, 1),
(2, 'Medyansah Eka Saputra', 2016, 'PT. Pertamina EP Asset 2 Field Pendopo', 3, 'Assalamu\'alaikum Warahmatullahi Wabarakatuh, Salam Akamigas! saya  alumni Politeknik Akamigas Palembang, saat ini saya bekerja di PT. Pertamina EP Asset 2 Field Pendopo, saya sangat bersyukur lulus dari Politeknik Akamigas Palembang.', 2, 1),
(3, 'Anwar Rifa\'i', 2014, 'PT. Tropik Energi Pandan', 1, 'Saat ini saya bekerja di PT. Tropik Energi Pandan sebagai seorang laboratorium analis, yaitu salah satu perusahan minyak bumi.', 3, 1),
(4, 'Faris Purbalesmana', 2017, 'PT. Pertamina RU IV Cilacap', 2, 'Assalamu\'alaikum Warahmatullahi Wabarakatuh, saat ini saya bekerja di PT. Pertamina RU IV Cilacap ', 4, 1),
(5, 'Pranita Apriana Sari', 2015, 'PT. Bukit Asam, Tbk', 4, 'Politeknik Akamigas Palembang yang bededikasi untuk mendidik mahasiswa-mahasiswanya menjadi mahasiswa yang kompeten dibidangnya, dan juga selama saya menjadi mahasiswa di sana Politeknik Akamigas Palembang adalah salah satu kampus yang sangat disiplin menurut saya dan itu melatih saya selama saat saya masuk di Politeknik Akamigas Palembang.', 5, 1);

